for f in original-source-NOT_IGNORED/browser_use/**/*.py; do if [[ $f != *"__init__.py"* && $f != *"test"* && $f != *"tests.py"* ]]; then dst="src/${f#original-source-NOT_IGNORED/browser_use/}"; dst="${dst%.py}.ts"; mkdir -p "$(dirname "$dst")"; echo "// Python source reference:" > "$dst"; echo "//" >> "$dst"; while IFS= read -r line; do echo "// $line" >> "$dst"; done < "$f"; echo "" >> "$dst"; echo "// TypeScript implementation:" >> "$dst"; echo "Converting $f to $dst"; fi; done
